/*Table structure for table `article` */

DROP TABLE IF EXISTS `article`;

CREATE TABLE `article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` int(11) DEFAULT NULL,
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `label` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `old_slugs` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci,
  `description` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `image` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `image_path` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `page_title` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_title` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_keywords` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `h1` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `view_count` int(11) DEFAULT '0',
  `like_count` int(11) DEFAULT '0',
  `comment_count` int(11) DEFAULT '0',
  `share_count` int(11) DEFAULT '0',
  `created_at` int(11) NOT NULL,
  `updated_at` int(11) DEFAULT NULL,
  `created_by` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `updated_by` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `auth_alias` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `is_hot` tinyint(1) DEFAULT NULL,
  `sort_order` int(11) DEFAULT NULL,
  `status` tinyint(2) DEFAULT NULL,
  `long_description` text COLLATE utf8_unicode_ci,
  `published_at` int(11) NOT NULL,
  `is_active` tinyint(1) DEFAULT NULL,
  `type` tinyint(2) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`),
  KEY `category_id` (`category_id`),
  CONSTRAINT `article_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `article_category` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=942 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `article` */

insert  into `article`(`id`,`category_id`,`name`,`label`,`slug`,`old_slugs`,`content`,`description`,`image`,`image_path`,`page_title`,`meta_title`,`meta_keywords`,`meta_description`,`h1`,`view_count`,`like_count`,`comment_count`,`share_count`,`created_at`,`updated_at`,`created_by`,`updated_by`,`auth_alias`,`is_hot`,`sort_order`,`status`,`long_description`,`published_at`,`is_active`,`type`) values (903,180,'Veneto chính thức khai trương showroom đầu tiên tại Sài Gòn','','veneto-chinh-thuc-khai-truong-showroom-dau-tien-tai-sai-gon-2248-28243-article','{\"1476802412\":\"veneto-chinh-thuc-khai-truong-showroom-dau-tien-tai-sai-gon\"}','<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\"><span style=\"font-size:26px\"><strong>B</strong></span>ằng sự nỗ lực của m&igrave;nh,&nbsp;<strong>Veneto&nbsp;</strong>lu&ocirc;n cố gắng kh&ocirc;ng ngừng trong việc cải tiến chất lượng sản phẩm v&agrave; n&acirc;ng cao chất lượng dịch vụ. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i lu&ocirc;n ch&uacute; trọng việc lắng nghe t&acirc;m tư, nguyện vọng của kh&aacute;ch h&agrave;ng để mở rộng mạng lưới chi nh&aacute;nh - mang sản phẩm tới gần hơn b&agrave;n tay người ti&ecirc;u d&ugrave;ng tr&ecirc;n khắp cả nước.<br />\r\n<br />\r\nThực thi sứ mệnh đ&oacute; v&agrave; cũng để thay lời cảm ơn sự ủng hộ của kh&aacute;ch h&agrave;ng ph&iacute;a Nam trong những năm qua,&nbsp;<strong>Veneto</strong>&nbsp;h&acirc;n hoan khai trương showroom thứ 9 của m&igrave;nh tại 316 L&ecirc; Văn Sỹ - Quận 3 &ndash; TP.HCM.</span><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span></p>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><span style=\"font-size:14px\"><img alt=\"\" src=\"//veneto.vn/images/201610/v7_0701164458.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/v3_0701164720.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/v6_0701164538.jpg\" style=\"border:none; height:auto !important; max-width:100%\" /></span></div>\r\n\r\n<p><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">Chắc chắn đ&acirc;y l&agrave; một tin vui d&agrave;nh cho những qu&yacute; kh&aacute;ch h&agrave;ng tại S&agrave;i G&ograve;n đ&atilde; lu&ocirc;n tin tưởng v&agrave; ủng hộ c&aacute;c sản phẩm thời trang nam của Veneto. Từ nay, c&aacute;c đấng m&agrave;y r&acirc;u của S&agrave;i Th&agrave;nh kh&ocirc;ng c&ograve;n phải chờ đợi qu&atilde;ng thời gian d&agrave;i chuyển ph&aacute;t nhanh qua đường bưu điện mới được tận mắt trải nghiệm sản phẩm. C&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; thể tới trực tiếp showroom cảm nhận chất liệu ri&ecirc;ng biệt của Veneto, thưởng thức mẫu m&atilde; của c&aacute;c bộ sưu tập v&agrave; mua sắm trực tiếp.</span><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span></p>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><span style=\"font-size:14px\"><a href=\"//veneto.vn/images/201610/v5_0701164405.jpg\" style=\"text-decoration: none;\"><img alt=\"\" src=\"//veneto.vn/images/201610/v5_0701164405.jpg\" style=\"height:467px; max-width:100%; width:700px\" /></a></span></div>\r\n\r\n<p><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">Trong kh&ocirc;ng kh&iacute; khai trương showroom n&aacute;o nhiệt, chị&nbsp;<strong>Đinh Ngọc Phượng (</strong>GĐ trẻ của nh&atilde;n hiệu thời trang&nbsp;<strong>Veneto)</strong>&nbsp;đ&atilde; kh&ocirc;ng giấu được sự hạnh ph&uacute;c khi chia sẻ về lần Nam tiến đầu ti&ecirc;n n&agrave;y của m&igrave;nh:&nbsp;<em>&ldquo;T&ocirc;i xin được gửi lời cảm ơn ch&acirc;n th&agrave;nh sự ủng hộ của c&aacute;c kh&aacute;ch h&agrave;ng ph&iacute;a Nam trong suốt qu&atilde;ng thời gian vừa qua. Để đ&aacute;p lại sự mong mỏi của anh chị, nay Veneto đ&atilde; đặt ch&acirc;n tới S&agrave;i G&ograve;n. Với th&agrave;nh c&ocirc;ng bước đầu n&agrave;y, t&ocirc;i kh&ocirc;ng coi trọng số lượng đại l&yacute; m&agrave; Veneto sẽ mở l&agrave; bao nhi&ecirc;u? M&agrave; phải l&agrave; chất lượng của từng showroom sẽ như thế n&agrave;o? C&oacute; thực sự l&agrave;m h&agrave;i l&ograve;ng kh&aacute;ch đến mua sắm hay kh&ocirc;ng? Xuất ph&aacute;t từ hy vọng đ&oacute;, tối v&agrave; từng nh&acirc;n vi&ecirc;n của Veneto sẽ lu&ocirc;n cố gắng v&agrave; ho&agrave;n thiện m&igrave;nh để trở th&agrave;nh một doanh nghiệp kh&ocirc;ng chỉ cung cấp sản phẩm tốt &ndash; m&agrave; c&ograve;n đi đầu về chất lượng phục vụ kh&aacute;ch h&agrave;ng&rdquo;</em></span><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span></p>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><span style=\"font-size:14px\"><em><img alt=\"\" src=\"//veneto.vn/images/201610/v1_0701164604.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/v2_0701164629.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/v4_0701164653.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></em></span></div>\r\n\r\n<p><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">Với một chặng đường d&agrave;i x&acirc;y dựng v&agrave; ph&aacute;t triển, Veneto rất vui mừng v&igrave; lu&ocirc;n c&oacute; sự đồng h&agrave;nh, ủng hộ v&agrave; g&oacute;p &yacute; của c&aacute;c qu&yacute; kh&aacute;ch h&agrave;ng &ndash; ch&iacute;nh nhờ động lực vững v&agrave;ng ấy, ch&uacute;ng t&ocirc;i kh&ocirc;ng nhừng ho&agrave;n thiện sản phẩm cả về chất liệu, mẫu m&atilde; lẫn phong c&aacute;ch phục vụ; song h&agrave;nh với đ&oacute; l&agrave; c&aacute;c chương tr&igrave;nh ưu đ&atilde;i, hậu m&atilde;i sau b&aacute;n h&agrave;ng,&hellip;<br />\r\n<br />\r\nĐể ch&agrave;o mừng sự kiện đặc biệt n&agrave;y,&nbsp;<strong>Veneto</strong>&nbsp;gửi đến c&aacute;c qu&yacute; kh&aacute;ch h&agrave;ng đến mua sắm tại showroom Veneto 316 L&ecirc; Văn Sỹ chương tr&igrave;nh giảm gi&aacute; v&ocirc; c&ugrave;ng hấp dẫ &ldquo;Sale off 15% tất cả sản phẩm&rdquo; &ndash; k&eacute;o d&agrave;i duy nhất trong tuần đầu khai trương (từ ng&agrave;y 03/01 - 10/01/2015).</span></p>\r\n','Bằng sự nỗ lực của mình, Veneto luôn cố gắng không ngừng trong việc cải tiến chất lượng sản phẩm và nâng cao chất lượng dịch vụ. Bên cạnh đó, chúng tôi luôn chú trọng việc lắng nghe tâm tư, nguyện vọng của khách hàng để mở rộng mạng lưới chi nhánh - mang sản phẩm tới gần hơn bàn tay người tiêu dùng trên khắp cả nước','BANNER_LVS_0701202144.jpg','/201610/','Veneto chính thức khai trương showroom đầu tiên tại Sài Gòn','Veneto chính thức khai trương showroom đầu tiên tại Sài Gòn',' veneto, khai trương, Sài Gòn, TP. HCM, Lê Văn Sỹ','Bằng sự nỗ lực của mình, Veneto luôn cố gắng không ngừng trong việc cải tiến chất lượng sản phẩm và nâng cao chất lượng dịch vụ. Bên cạnh đó, chúng tôi luôn chú trọng việc lắng nghe tâm tư, nguyện vọng của khách hàng để mở rộng mạng lưới chi nhánh - mang sản phẩm tới gần hơn bàn tay người tiêu dùng trên khắp cả nước','Veneto chính thức khai trương showroom đầu tiên tại Sài Gòn',0,0,0,0,1476430873,1476802412,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476430744,1,NULL),(904,180,'Veneto hân hoan khai trương showroom thứ 10 trên toàn quốc - 19 Phố Huế','','veneto-han-hoan-khai-truong-showroom-thu-tren-toan-quoc-–-pho-hue-2248-28244-article','{\"1476802467\":\"veneto-han-hoan-khai-truong-showroom-thu-10-tren-toan-quoc-19-pho-hue\",\"1476802512\":\"veneto-han-hoan-khai-truong-showroom-thu-tren-toan-quoc-%E2%80%93-pho-hue-2248-28244-article\"}','<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:14px\">Được sự y&ecirc;u mến của h&agrave;ng ng&agrave;n kh&aacute;ch h&agrave;ng,&nbsp;<strong>Veneto&nbsp;</strong>lu&ocirc;n dẫn đầu thị trường thời trang nam với phong c&aacute;ch lịch l&atilde;m, thời thượng. C&aacute;c sản phẩm thời trang của Veneto lu&ocirc;n hướng tới sự trẻ trung v&agrave; đẳng cấp; đặc biệt l&agrave; sản phẩm suit, thời trang c&ocirc;ng sở. Để thực hiện tốt mục ti&ecirc;u phục vụ tốt nhất nhu cầu mặc đẹp của c&aacute;c đấng m&agrave;y r&acirc;u, Veneto lu&ocirc;n nỗ lực hết m&igrave;nh n&acirc;ng cao chất lượng sản phẩm &amp; dịch vụ. B&ecirc;n cạnh đ&oacute;, thương hiệu Veneto kh&ocirc;ng ngừng vươn m&igrave;nh, mở rộng mạng lưới chi nh&aacute;nh tr&ecirc;n khắp cả nước; đưa c&aacute;c sản phẩm thời trang cao cấp tới gần hơn b&agrave;n tay người ti&ecirc;u d&ugrave;ng.</span><br />\r\n<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81101_compressed_2404185051.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81375_2404192348.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81482_2404192513.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<em><span style=\"font-size:14px\">Kh&ocirc;ng kh&iacute; rộn r&agrave;ng tại lễ khai trương</span></em><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Thực thi sứ mệnh n&agrave;y, sau một thời gian nỗ lực chuẩn bị &amp; ho&agrave;n thiện mặt bằng, chiều qua 23/04/2016, chương tr&igrave;nh ra mắt showroom&nbsp;<strong>Veneto</strong>&nbsp;thứ 10 (19 Phố Huế - H&agrave; Nội) đ&atilde; diễn ra tưng bừng, long trọng với sự tham gia của rất nhiều gương mặt nổi tiếng: diễn vi&ecirc;n &ndash; đại sứ thương hiệu&nbsp;<strong>Phạm Anh Tuấn</strong>; ch&agrave;ng hot boy - diễn vi&ecirc;n trẻ&nbsp;<strong>B&igrave;nh An</strong>; &Aacute; qu&acirc;n&nbsp;Vietnam&#39;s Next Top Model 2014&nbsp;<strong>Ti&ecirc;u Ngọc Linh</strong>;&nbsp;</span><span style=\"font-size:14px\">&Aacute; qu&acirc;n&nbsp;Vietnam&#39;s Next Top Model 2014&nbsp;<strong>Phạm Anh Duy;&nbsp;</strong><strong>Mai Tuấn Anh</strong>&nbsp;- Giải v&agrave;ng Si&ecirc;u mẫu 2015;&nbsp;<strong>Đồng &Aacute;nh Quỳnh</strong>&nbsp;- Top 5 Hoa hậu t&agrave;i năng Việt Nam 2014;&nbsp;<strong>Vũ Tiến Mạnh</strong>&nbsp;- Top 6 Nh&agrave; thiết kế thời trang Việt Nam;&nbsp;Vietnam&#39;s Next Top Model 2014<strong>&nbsp;Kim Ng&acirc;n</strong>; 2 ch&agrave;ng người mẫu trẻ&nbsp;<strong>Ho&agrave;ng Anh T&uacute;</strong>&nbsp;&amp;&nbsp;<strong>Ho&agrave;ng Gia Anh Vũ</strong>&nbsp;-&nbsp;Vietnam&#39;s Next Top Model&nbsp; 2015</span><span style=\"font-size:14px\">&nbsp;&hellip; c&ugrave;ng qu&yacute; bạn h&agrave;ng, qu&yacute; đối t&aacute;c v&agrave; c&aacute;c kh&aacute;ch h&agrave;ng th&acirc;n thiết, đ&atilde; gắn b&oacute; cũng Veneto trong suốt thời gian qua.&nbsp;<br />\r\n<br />\r\n<br />\r\n<em><strong>Một số h&igrave;nh ảnh kh&aacute;c mời tham dự chương tr&igrave;nh:</strong></em></span><br />\r\n<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><em><strong><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81719_2404191223.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81795_2404191400.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></strong></em><br />\r\n<br />\r\n<br />\r\n<br />\r\n<em><strong><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81975_2404191448.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></strong></em><br />\r\n<br />\r\n<em><strong><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81312_2404191420.jpg\" style=\"border:none; height:1049px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81072_2404191527.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></strong></em><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Tọa lạc tại vị tr&iacute; đẹp c&ugrave;ng với kh&ocirc;ng gian mua sắn sang trọng, rộng r&atilde;i, showroom&nbsp;<strong>Veneto 19 Phố Huế&nbsp;</strong>đ&atilde; nhận được những lời khen ngợi kh&ocirc;ng ngớt v&agrave; những t&igrave;nh cảm y&ecirc;u mến, sự ủng hộ nhiệt t&igrave;nh của kh&aacute;ch mời &amp; c&aacute;c kh&aacute;ch h&agrave;ng tới tham quan v&agrave; mua sắm. Trong thiết kế của showroom thứ 10,&nbsp;<strong>Veneto</strong>&nbsp;mang tới một sự thay đổi đặc biệt trong diện mạo, phong c&aacute;ch decor của showroom kh&iacute; &aacute;p dụng phi&ecirc;n bản thiết kế 3.0 đầy tinh tế v&agrave; đẹp mắt. Bằng những nỗ lực v&agrave; thay đổi tuy nhỏ đ&oacute;, gi&aacute;m đốc v&agrave; đội ngũ nh&acirc;n vi&ecirc;n ch&uacute;ng t&ocirc;i hy vọng: sự phục vụ chuy&ecirc;n nghiệp kết hợp với một m&agrave;u sắc rất ri&ecirc;ng chỉ c&oacute; tại Veneto sẽ mang tới những trải nghiệm ho&agrave;n to&agrave;n mới, l&agrave;m h&agrave;i l&ograve;ng c&aacute;c qu&yacute; kh&aacute;ch h&agrave;ng.</span><br />\r\n<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><span style=\"font-size:14px\"><img alt=\"\" src=\"//veneto.vn/images/201610/13007104_553406391528782_5454479457029168646_n_2404191730.jpg\" style=\"border:none; height:394px; max-width:100%; width:700px\" /></span></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Chia sẻ tại buổi lễ ra mắt, chị&nbsp;<strong>Đinh Ngọc Phượng</strong>&nbsp;&ndash; gi&aacute;m đốc C&ocirc;ng ty TNHH Thời trang Veneto t&acirc;m sự:&nbsp;<em>&ldquo;Để c&oacute; được những bước tiến quan trọng như ng&agrave;y h&ocirc;m nay, Veneto đ&atilde; kh&ocirc;ng những nỗ lực, kh&ocirc;ng ngừng cố gắng trong hoạt động nghi&ecirc;n cứu thị trường &ndash; t&igrave;m hiểu nhu cầu kh&aacute;ch h&agrave;ng v&agrave; xu hướng ăn mặc đang thịnh h&agrave;nh. V&agrave; trong sự nỗ lực ấy, ch&uacute;ng t&ocirc;i gửi lời cảm ơn ch&acirc;n th&agrave;nh nhất đến c&aacute;c qu&yacute; đối t&aacute;c gi&uacute;p Veneto ng&agrave;y c&agrave;ng phủ s&oacute;ng, đưa sản phẩm của ch&uacute;ng t&ocirc;i đến gần hơn với kh&aacute;ch h&agrave;ng. Điều đặc biệt hơn, đ&oacute; l&agrave; sự tin tưởng v&agrave; ủng hộ từ ph&iacute;a kh&aacute;ch h&agrave;ng ti&ecirc;u d&ugrave;ng; để kh&ocirc;ng phụ l&ograve;ng mong mỏi của qu&yacute; kh&aacute;ch Veneto sẽ ng&agrave;y c&agrave;ng ho&agrave;n thiện v&agrave; cho ra mắt những sản phẩm cao cấp hơn, đẹp hơn để thỏa m&atilde;n tốt nhất nhu cầu của kh&aacute;ch h&agrave;ng&rdquo;</em></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><em><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81567_2404191814.jpg\" style=\"border:none; height:1050px; max-width:100%; width:700px\" /><br />\r\n<span style=\"font-size:14px\">Chị Đinh Ngọc Phượng - Gi&aacute;m đốc Veneto ph&aacute;t biểu tải buổi lễ</span></em><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81618_2504092416.jpg\" style=\"border:none; height:auto !important; max-width:100%; width:700px\" /><br />\r\n<em><span style=\"font-size:14px\">Chị Phạm Thu Trang - Đại l&yacute; chi nh&aacute;nh ph&aacute;t biểu cảm ơn v&agrave; ch&uacute;c mừng c&ocirc;ng ty</span></em></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81601_compressed_2404190022.jpg\" style=\"border:none; height:467px; line-height:20.8px; max-width:100%; width:700px\" /><br />\r\n<span style=\"font-size:14px\"><em>C&aacute;c đối t&aacute;c của Veneto n&acirc;ng ly rượu mừng</em></span></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Đồng h&agrave;nh c&ugrave;ng hoạt động khai trương Veneto 19 Phố Huế chiều qua,&nbsp;<strong>Veneto&nbsp;</strong>cho ra mắt bộ sưu tập&nbsp;<strong>&ldquo;Elegant Suit&rdquo;</strong>&nbsp;với điểm nhấn l&agrave; những gam m&agrave;u trầm huyền b&iacute;, chắc chắn sẽ m&ecirc; hoặc c&aacute;c đấng m&agrave;y r&acirc;u y&ecirc;u mến vẻ lịch l&atilde;m.</span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/_MI81956_2404191954.jpg\" style=\"border:none; height:1050px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81985_2404192021.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI81996_2404192039.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI82000_2404192102.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, chương tr&igrave;nh mini-game của Veneto cũng d&agrave;nh tặng những phần qu&agrave; th&uacute; vị cho người tham gia. Những gi&acirc;y ph&uacute;t vui tươi, sảng kho&aacute;i để lại dấu ấn tr&ecirc;n từng khu&ocirc;n mặt kh&aacute;ch h&agrave;ng c&oacute; mặt tại buổi ra mắt.</span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/_MI82121_2404192124.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/_MI82064_2404192148.jpg\" style=\"border:none; height:467px; max-width:100%; width:700px\" /></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Ch&uacute;ng t&ocirc;i kỳ vọng trong tương lai gần, sẽ c&oacute; th&ecirc;m những lễ khai trương showroom tiếp theo của Veneto trải d&agrave;i tr&ecirc;n khắp chiều d&agrave;i Việt Nam để c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; thể trải nghiệm sản phẩm một c&aacute;ch h&agrave;i l&ograve;ng, thuận tiện nhất.</span>\r\n\r\n<div style=\"text-align: right;\"><span style=\"font-size:14px\"><strong>Veneto &ndash; Khởi đầu cho sự th&agrave;nh c&ocirc;ng!</strong></span></div>\r\n</div>\r\n','Chiều qua 23/04/2016, chương trình ra mắt showroom Veneto thứ 10 (19 Phố Huế - Hà Nội) đã diễn ra tưng bừng','javitex_khaitruong_phohue_compressed_2707144813.jpg','/201610/','Veneto hân hoan khai trương showroom thứ 10 trên toàn quốc - 19 Phố Huế','Veneto hân hoan khai trương showroom thứ 10 trên toàn quốc - 19 Phố Huế','khai trương, Veneto 19 Phố Huế, thời trang nam, thời trang công sở, thời trang Veneto','Chiều qua 23/04/2016, chương trình ra mắt showroom Veneto thứ 10 (19 Phố Huế - Hà Nội) đã diễn ra tưng bừng','Veneto hân hoan khai trương showroom thứ 10 trên toàn quốc - 19 Phố Huế',0,0,0,0,1476431171,1476802512,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431044,1,NULL),(905,178,'Theo chân Veneto đến với trẻ em vùng cao','','theo-chan-veneto-den-voi-tre-em-vung-cao-2237-28245-article','{\"1476802544\":\"theo-chan-veneto-den-voi-tre-em-vung-cao\"}','<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">Những ng&agrave;y cuối th&aacute;ng Hai, trong tiết trời vừa sang mới xu&acirc;n, với sự gi&uacute;p đỡ nhiệt t&igrave;nh của Hội Chữ thập đỏ tỉnh Bắc Kạn; tập thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n&nbsp;<strong>C&ocirc;ng ty TNHH Thời trang&nbsp;‪Veneto</strong>‬ đ&atilde; vượt nẻo đường xa x&ocirc;i đến với x&atilde; Bản Thi.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong0_0903102812.jpg\" style=\"border:none; height:433px; max-width:100%; width:650px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong1_0903102821.jpg\" style=\"border:none; height:464px; max-width:100%; width:650px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">Tại bản l&agrave;ng v&ugrave;ng cao như x&atilde; Bản Thi</span></span><span style=\"color:rgb(0, 0, 0); font-size:14px\">, huyện Chợ Đồn, tỉnh Bắc Kạn chỉ</span><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">&nbsp;c&oacute; 2 trường tiểu học v&agrave; mầm non. Trường tiểu học c&oacute; 133 em th&igrave; c&oacute; tới 83 em thuộc diện kh&oacute; khăn; trường mầm non 156 em th&igrave; c&oacute; 20 em diện ngh&egrave;o. Qua trao đổi với c&ocirc; gi&aacute;o Ph&oacute; hiệu trưởng c&ugrave;ng c&aacute;c thầy c&ocirc; gia đ&igrave;nh, c&aacute;c em nhỏ tại v&ugrave;ng cao nơi đ&acirc;y vẫn chưa thực sự coi trọng việc học tập. Một thực tại đ&aacute;ng buồn hơn đ&oacute; l&agrave; kh&ocirc;ng &iacute;t số hộ gia đ&igrave;nh cho con em nghỉ học rất sớm để lao động phụ gi&uacute;p cha mẹ l&agrave;m nương rẫy...</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong2_0903102828.jpg\" style=\"border:none; height:463px; max-width:100%; width:650px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong7_0903102959.jpg\" style=\"border:none; height:586px; max-width:100%; width:650px\" /><br />\r\n<span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>C&aacute;c em nhỏ tại x&atilde; Bản Thi</em></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong99_0903103234.jpg\" style=\"border:none; height:480px; max-width:100%; width:650px\" /><br />\r\n<span style=\"font-size:14px\"><em>Người mẹ kh&ocirc;ng giấu nổi niềm x&uacute;c động khi đo&agrave;n tới thăm gia đ&igrave;nh</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">Phần lớn c&aacute;c em nhỏ tại x&atilde; Bản Thi đều c&oacute; ho&agrave;n cảnh kh&aacute; kh&oacute; khăn. Những m&oacute;n đồ nhỏ như: cặp s&aacute;ch, b&uacute;t, vở, tất, quần &aacute;o,... m&agrave; đo&agrave;n trao tặng thật sự đ&aacute;ng qu&yacute; với c&aacute;c em.&nbsp;</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong3_0903103154-2.jpg\" style=\"border:none; height:433px; max-width:100%; width:650px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong6_0903103213.jpg\" style=\"border:none; height:433px; max-width:100%; width:650px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong8_0903103220.jpg\" style=\"border:none; height:433px; max-width:100%; width:650px\" /></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong9_0903103228.jpg\" style=\"border:none; height:975px; max-width:100%; width:650px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<br />\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">&quot;Nh&igrave;n những nụ cười vui mừng, rạng rỡ tr&ecirc;n khu&ocirc;n mặt c&aacute;c em; ngắm nh&igrave;n c&aacute;ch c&aacute;c em khoe nhau những m&oacute;n đồ mới - ch&uacute;ng t&ocirc;i như sống lại một thời thơ ấu; lắng nghe thầy c&ocirc; t&acirc;m sự về ho&agrave;n cảnh nhiều em phải nghỉ học đi nương l&agrave;m rẫy theo &yacute; của gia đ&igrave;nh khiến cả đo&agrave;n ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng cảm động. Vậy mới thấu hiểu những gian kh&oacute;, vất vả của c&aacute;c em v&ugrave;ng cao&quot; - một bạn nh&acirc;n vi&ecirc;n trong đo&agrave;n thiện nguyện chia sẻ.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong4_0903103201.jpg\" style=\"border:none; color:rgb(0, 0, 0); font-size:14px; height:451px; line-height:16.08px; max-width:100%; text-align:justify; width:650px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_backan_xuanyeuthuong5_0903103101.jpg\" style=\"border:none; height:433px; max-width:100%; width:650px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">Veneto mong rằng những m&oacute;n qu&agrave; nhỏ trong chương tr&igrave;nh</span></span><span style=\"color:rgb(0, 0, 0); font-size:14px\">&nbsp;&quot;Xu&acirc;n y&ecirc;u thương&quot; sẽ</span><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">&nbsp;phần n&agrave;o kh&iacute;ch lệ được tinh thần học tập của c&aacute;c em, trao hi vọng cho cha mẹ &amp; ch&iacute;nh c&aacute;c em để c&oacute; thể tiếp tục cho c&aacute;c em đến trường.&nbsp;Hy vọng đ&oacute; sẽ l&agrave; nguồn động vi&ecirc;n tinh thần để c&aacute;c em chăm ngoan, tiếp tục đến trường. Cầu ch&uacute;c cho c&aacute;c em học tập tốt để trở th&agrave;nh người c&oacute; &iacute;ch cho x&atilde; hội tương lai!!&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\"><strong>Veneto</strong></span></span></p>\r\n','Những ngày cuối tháng Hai, tập thể cán bộ, nhân viên Công ty TNHH Thời trang ‪Veneto‬ đã vượt nẻo đường xa xôi đến với xã Bản Thi.','veneto_backan_xuanyeuthuong3_0903103154.jpg','/201610/','Theo chân Veneto đến với trẻ em vùng cao','Theo chân Veneto đến với trẻ em vùng cao','Bắc Kạn, xuân yêu thương, trẻ em, xã Bản Thi, veneto','Những ngày cuối tháng Hai, tập thể cán bộ, nhân viên Công ty TNHH Thời trang ‪Veneto‬ đã vượt nẻo đường xa xôi đến với xã Bản Thi.','Theo chân Veneto đến với trẻ em vùng cao',0,0,0,0,1476431299,1476802544,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431183,1,NULL),(906,180,'Veneto tưng bừng khai trương chi nhánh Hải Phòng','','veneto-tung-bung-khai-truong-chi-nhanh-hai-phong-2248-28246-article','{\"1476802572\":\"veneto-tung-bung-khai-truong-chi-nhanh-hai-phong\"}','<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hướng tới phong c&aacute;ch thời trang lịch l&atilde;m, thời thượng v&agrave; đẳng cấp,&nbsp;</span></span><strong>Veneto</strong><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>&nbsp;</strong>được biết đến như lựa chọn h&agrave;ng đầu d&agrave;nh cho nam giới c&ocirc;ng sở. Với mục ti&ecirc;u phục vụ tốt nhất nhu cầu mặc đẹp của nam giới,&nbsp;<strong>Veneto</strong>&nbsp;kh&ocirc;ng ngừng mở rộng mạng lưới hoạt động của m&igrave;nh tr&ecirc;n phạm vi trong v&agrave; ngo&agrave;i nước; cũng như t&igrave;m hiểu, thăm d&ograve; nhu cầu, thị hiếu thời trang của tất cả c&aacute;c đối tượng kh&aacute;ch h&agrave;ng.<br />\r\n<br />\r\nVới hy vọng mang c&aacute;c sản phẩm thời trang với phong c&aacute;ch v&agrave; trẻ trung đến gần hơn qu&yacute; kh&aacute;ch h&agrave;ng; đồng thời với phương hướng mở rộng thị trường, t&igrave;m kiếm đối t&aacute;c tiềm năng tận t&acirc;m với thương hiệu để c&ugrave;ng ph&aacute;t triển.&nbsp;<strong>Veneto</strong>&nbsp;tưng bừng khai trương Showroom mới tại miền đất Cảng Hải Ph&ograve;ng&nbsp;<em>(Địa chỉ: 52 M&ecirc; Linh- L&ecirc; Ch&acirc;n - Hải Ph&ograve;ng)</em>&nbsp;&ndash; đ&aacute;nh dấu cột mốc chi nh&aacute;nh thứ 5 tr&ecirc;n cả nước.</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0358_1410165601.JPG\" style=\"border:none; height:641.578px; max-width:100%; width:1039px\" /><br />\r\n<em><span style=\"font-size:16px\">Gi&aacute;m đốc v&agrave; kh&aacute;ch mời cắt băng khai trương Showroom</span></em></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0149_1410170742.JPG\" style=\"border:none; height:718.203px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0226_1410170757.JPG\" style=\"border:none; height:690.922px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0171_1410170809.JPG\" style=\"border:none; height:777.938px; line-height:20.8px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0174_1410170821.JPG\" style=\"border:none; height:1435.11px; max-width:100%; width:1039px\" /><br />\r\n<em><span style=\"font-size:16px\">Một số kh&aacute;ch mời tham dự chương tr&igrave;nh khai trương</span></em></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sau 10 ng&agrave;y gấp r&uacute;t ho&agrave;n thiện, ng&agrave;y 10/8/2015 Showroom&nbsp;<strong>Veneto Hải Ph&ograve;ng</strong>&nbsp;đ&atilde; ch&iacute;nh thức đi v&agrave;o hoạt động. Với sự ủng hộ đ&ocirc;ng đảo của c&aacute;c vị kh&aacute;ch mời v&agrave; qu&yacute; kh&aacute;ch h&agrave;ng tới tham dự, chương tr&igrave;nh khai trương đ&atilde; diễn ra tưng bừng, n&aacute;o nhiệt.&nbsp;<strong>Veneto Hải Ph&ograve;ng</strong>&nbsp;d&agrave;nh được những lời khen ngợi v&agrave; những t&igrave;nh cảm y&ecirc;u mến của c&aacute;c kh&aacute;ch h&agrave;ng tới tham quan v&agrave; mua sắm. Với th&agrave;nh c&ocirc;ng n&agrave;y,&nbsp;<strong>Veneto&nbsp;</strong>đ&atilde; thực sự toả s&aacute;ng! Ch&uacute;ng t&ocirc;i hy vọng&nbsp;<strong>Veneto Hải Ph&ograve;ng</strong>&nbsp;c&oacute; thể đ&aacute;p ứng một c&aacute;ch tốt nhất nhu cầu mua sắm c&aacute;c sản phẩm thời trang như: veston thời trang, vest cưới, sơ mi c&ocirc;ng sở, quần &acirc;u, đồng phục c&ocirc;ng sở&hellip; của qu&yacute; kh&aacute;ch h&agrave;ng tại Hải Ph&ograve;ng v&agrave; c&aacute;c khu vực l&acirc;n cận.</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0184_1410165845.JPG\" style=\"border:none; height:690.922px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0190_1410170859.JPG\" style=\"border:none; height:691.844px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0211_1410170913.JPG\" style=\"border:none; height:690.922px; max-width:100%; width:1039px\" /></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đồng h&agrave;nh c&ugrave;ng sự kiện ra mắt Showroom&nbsp;<strong>Veneto Hải Ph&ograve;ng</strong>, ch&uacute;ng t&ocirc;i d&agrave;nh tặng rất nhiều chương tr&igrave;nh ưu đ&atilde;i khai trương hấp dẫn cho kh&aacute;ch h&agrave;ng đến mua sắm. Đặc biệt l&agrave; chương tr&igrave;nh:</span></span></div>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tặng sơmi bất kỳ khi mua 1 bộ vest</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Giảm 20 % cho ho&aacute; đơn tr&ecirc;n 999k</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Giảm gi&aacute; 10% tất cả c&aacute;c sản phẩm</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span></p>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"//veneto.vn/images/201610/DSC_0220_1410170927.JPG\" style=\"border:none; height:698.719px; max-width:100%; width:1039px\" /><br />\r\n<em>Đội ngũ nh&acirc;n vi&ecirc;n trẻ trung tại Veneto Hải Ph&ograve;ng</em></span></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">&nbsp;Ch&uacute;ng t&ocirc;i kỳ vọng trong tương lai gần, thương hiệu&nbsp;<strong>Veneto</strong>&nbsp;sẽ c&oacute; mặt ở khắp c&aacute;c tỉnh th&agrave;nh để c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; thể trải nghiệm sản phẩm một c&aacute;ch h&agrave;i l&ograve;ng, thuận tiện nhất</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: right;\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Veneto &ndash; Khởi đầu cho sự th&agrave;nh c&ocirc;ng!</strong></span></span></div>\r\n','Veneto tưng bừng khai trương Showroom mới tại miền đất Cảng Hải Phòng (Địa chỉ: 52 Mê Linh- Lê Chân - Hải Phòng) – đánh dấu cột mốc chi nhánh thứ 5 trên cả nước.','khai_truong_veneto_HP_2210101446_1006233026.jpg','/201610/','Veneto tưng bừng khai trương chi nhánh Hải Phòng','Veneto tưng bừng khai trương chi nhánh Hải Phòng','','Veneto tưng bừng khai trương Showroom mới tại miền đất Cảng Hải Phòng (Địa chỉ: 52 Mê Linh- Lê Chân - Hải Phòng) – đánh dấu cột mốc chi nhánh thứ 5 trên cả nước.','Veneto tưng bừng khai trương chi nhánh Hải Phòng',0,0,0,0,1476431466,1476802572,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431333,1,NULL),(907,181,'Veneto ưu đãi cực shock: mua vest tặng sơmi','','veneto-uu-dai-cuc-shock-mua-vest-tang-somi-2249-28248-article','{\"1476431671\":\"veneto-uu-dai-cuc-shock-mua-vest-tang-somi\"}','<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><img alt=\"\" src=\"//veneto.vn/images/201610/mua-vest-tang-somi-veneto_2403150611-2.jpg\" style=\"border:none; height:284px; max-width:100%; width:700px\" /></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\">\r\n<p><br />\r\nĐể mang tới vẻ lịch l&atilde;m, kh&aacute;c biệt cho c&aacute;c ch&agrave;ng trong ng&agrave;y cưới; để anh em c&ocirc;ng sở c&oacute; th&ecirc;m những sự lựa chọn mới mỗi ng&agrave;y.</p>\r\n\r\n<p><a class=\"_58cn\" href=\"https://www.facebook.com/hashtag/veneto?source=feed_text&amp;story_id=543115659224522\" style=\"text-decoration: none; color: rgb(59, 89, 152); cursor: pointer;\"><span style=\"color:rgb(98, 122, 173)\">‪#&lrm;</span>Veneto‬</a>&nbsp;quyết định gửi tới c&aacute;c kh&aacute;ch h&agrave;ng chương tr&igrave;nh ưu đ&atilde;i đ&atilde; từng &ldquo;l&agrave;m mưa l&agrave;m gi&oacute;&rdquo; tại c&aacute;c showroom trong năm vừa qua. Cụ thể như sau:</p>\r\n\r\n<div class=\"text_exposed_show\" style=\"display: inline; color: rgb(20, 24, 35); font-family: helvetica, arial, sans-serif; font-size: 14px; line-height: 19.32px;\">\r\n<p><br />\r\nKhi mua&nbsp;<strong>01 BỘ VEST BẤT KỲ</strong>&nbsp;tại c&aacute;c showroom của Veneto, qu&yacute; kh&aacute;ch sẽ được tặng&nbsp;<strong>01 SƠMI CAO CẤP</strong></p>\r\n\r\n<p>H&agrave;ng ng&agrave;n kh&aacute;ch h&agrave;ng đ&atilde; h&agrave;i l&ograve;ng v&agrave; tin tưởng v&agrave;o sản phẩm Veston của Veneto, h&atilde;y nhanh ch&acirc;n tới Veneto mua sắm v&agrave; tận hưởng ưu đ&atilde;i cực hấp dẫn n&agrave;y&nbsp;</p>\r\n\r\n<p><br />\r\n-----<br />\r\n&nbsp;LƯU &Yacute;:<br />\r\n■ Thời gian ưu đ&atilde;i: 23/3 &ndash; 30/03/2016<br />\r\n■ Chương tr&igrave;nh &aacute;p dụng cho to&agrave;n bộ hệ thống showroom tr&ecirc;n to&agrave;n quốc<br />\r\n■ Chương tr&igrave;nh kh&ocirc;ng &aacute;p dụng chung với thẻ VIP v&agrave; c&aacute;c dịch vụ khuyến m&atilde;i kh&aacute;c.</p>\r\n\r\n<p><br />\r\n-----<br />\r\n&nbsp;ALBUM VESTON ĐẲNG CẤP CỦA VENETO xem tại:<br />\r\n■ Autumn 2015 Collection:&nbsp;<a href=\"https://www.facebook.com/l.php?u=https%3A%2F%2Fgoo.gl%2FlONjLI&amp;h=pAQEZlrGdAQHXDjzflyvI1ATgPjvodz9IL0SeFnmrlFHcKQ&amp;enc=AZPIeyfhv-khjJZs_d0EYkTeXMbzxcmS9bdC4WDCZprUmhLgv688JgBurSrmMPSBJ6qledNPo1bbT6pOueFg7oauIiYxWjxiFmC32YISFDf6mnz0h_hqvO9eKaBtu4DXLUVZAYD7kaCJOcHTD06itEJ4XrEOPg1K077yJ5GopA_OZxQKlIUJhAHnKHIlW8-8LrQ_5xZDI6-BpCz6EYuk4hv8&amp;s=1\" rel=\"nofollow\" style=\"text-decoration: none; color: rgb(59, 89, 152); cursor: pointer;\" target=\"_blank\">https://goo.gl/lONjLI</a><br />\r\n■ Winter 2015 Collection:&nbsp;<a href=\"https://goo.gl/Mt7BUe\" rel=\"nofollow\" style=\"text-decoration: none; color: rgb(59, 89, 152); cursor: pointer;\" target=\"_blank\">https://goo.gl/Mt7BUe</a><br />\r\n■ Veneto Wedding Fashion Show 2015:&nbsp;<a href=\"https://www.facebook.com/l.php?u=https%3A%2F%2Fgoo.gl%2FGGhokc&amp;h=kAQGJBNT-AQFh2bsmrWWWeVQARWpp7lyB2nFbGYR4NctQlA&amp;enc=AZN18Zb5pdyUsKvF9kVRR4RTUu_5ulnSiYoTp5q7NIo_v8j4ibT5-tWSobb_6LncHooMSGNfqZUbPRTjE6jj8uxaeG2A6AufvpIFpJCzucfPxRauMFrJXxpxOCGZdpodlQu5PIUyrBuvgn-QHinDM5FR2_tUrL5RqvSBNbO1JRdJ5GlYLMSM5Phn4bBYuJe7eu80jkSm4LbfXLmNdxZsojwX&amp;s=1\" rel=\"nofollow\" style=\"text-decoration: none; color: rgb(59, 89, 152); cursor: pointer;\" target=\"_blank\">https://goo.gl/GGhokc</a></p>\r\n</div>\r\n</div>\r\n','Veneto‬ quyết định gửi tới các khách hàng chương trình ưu đãi đã từng “làm mưa làm gió” tại các showroom trong năm vừa qua.','mua-vest-tang-somi-veneto_2403150611.jpg','/201610/','Veneto ưu đãi cực shock: mua vest tặng sơmi','Veneto ưu đãi cực shock: mua vest tặng sơmi','ưu đãi, khuyến mãi, promotion, veneto, thời trang nam, công sở, vest cưới','Veneto‬ quyết định gửi tới các khách hàng chương trình ưu đãi đã từng “làm mưa làm gió” tại các showroom trong năm vừa qua.','Veneto ưu đãi cực shock: mua vest tặng sơmi',0,0,0,0,1476431596,1476431671,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431471,1,NULL),(908,181,'Veneto dành tặng ưu đãi \"cực khủng\" cho khách hàng dịp cuối năm','','-2249-28249-article','{\"1476802632\":\"veneto-danh-tang-uu-dai-cuc-khung-cho-khach-hang-dip-cuoi-nam\"}','<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; clear: both; text-align: justify;\"><span style=\"font-size:14px\"><span style=\"font-size:26px\"><strong>M</strong></span>ột năm nữa lại sắp đi qua. Nh&acirc;n dịp Gi&aacute;ng sinh v&agrave; năm mới đang tới thật gần,&nbsp;<strong>Veneto</strong>&nbsp;đ&atilde; update th&ecirc;m thật nhiều sản phẩm mới tại c&aacute;c hệ thống showroom; c&ugrave;ng với h&agrave;ng trăm items cực hấp dẫn để phục vụ nhu cầu mua sắm của kh&aacute;ch h&agrave;ng nh&acirc;n dịp cuối năm 2015.&nbsp;</span><span style=\"font-size:14px\">Để nh&acirc;n đ&ocirc;i sự hấp dẫn của kh&ocirc;ng kh&iacute; mua sắm dịp cuối năm,&nbsp;<strong>Veneto&nbsp;</strong>triển khai chương tr&igrave;nh ưu đ&atilde;i hấp dẫn nhất 2015 để kh&aacute;ch h&agrave;ng thỏa sức mua sắm; v&agrave; cũng để thay lời cảm ơn tới qu&yacute; kh&aacute;ch h&agrave;ng th&acirc;n th&acirc;n thiết đ&atilde; ủng hộ&nbsp;<strong>Veneto</strong>&nbsp;trong suốt một năm vừa qua.</span></div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:14px\">&nbsp;<br />\r\nKhi mua 01 bộ vest bất kỳ từ ng&agrave;y&nbsp;<strong>15/12 - 30/12/2015</strong>&nbsp;tại c&aacute;c hệ thống showroom của Veneto tr&ecirc;n to&agrave;n quốc, qu&yacute; kh&aacute;ch sẽ được tặng ngay c&aacute;c sản phẩm cao cấp:</span></div>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">01 &aacute;o sơmi cao cấp</span></li>\r\n	<li><span style=\"font-size:14px\">01 caravat thời thượng</span></li>\r\n	<li><span style=\"font-size:14px\">01 d&acirc;y lưng thời trang</span></li>\r\n	<li><span style=\"font-size:14px\">01 thẻ VIP giảm gi&aacute; 10% tại c&aacute;c hệ thống showroom của Veneto tại H&agrave; Nội</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">B&ecirc;n cạnh đ&oacute;, Veneto gửi tới qu&yacute; kh&aacute;ch h&agrave;ng ưu đ&atilde;i v&ocirc; c&ugrave;ng hấp dẫn từ c&aacute;c thương hiệu li&ecirc;n kết:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">01 Voucher trị gi&aacute; 1.000.000đ từ thương hiệu v&aacute;y cưới Linh B&ugrave;i Bridal</span></li>\r\n	<li><span style=\"font-size:14px\">01 Voucher trị gi&aacute; 1.000.000đ từ Thời trang Dạ hội Queen Style</span></li>\r\n	<li><span style=\"font-size:14px\">01 thẻ vip 10% sử dụng tại c&aacute;c hệ thống tại H&agrave; Nội của Veneto</span></li>\r\n</ul>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><br />\r\n<span style=\"font-size:14px\"><img alt=\"\" src=\"//veneto.vn/images/201610/VENETO_POST_1712144132-2.jpg\" style=\"border:none; height:358px; max-width:100%; width:700px\" /></span></div>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">&nbsp;</span></p>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:14px\">Sử dụng những chất liệu tốt nhất, ch&uacute; trọng tới sự thoải m&aacute;i của ph&aacute;i mạnh khi mặc; kết hợp với đ&ocirc;i tay t&agrave;i hoa của những thợ may l&agrave;nh nghề; c&aacute;c sản phẩm veston của Veneto lu&ocirc;n nhận được sự ưa chuộng v&agrave; đ&aacute;nh gi&aacute; cao từ người ti&ecirc;u d&ugrave;ng. Veneto cam kết mang tới cho kh&aacute;ch h&agrave;ng một vẻ ngo&agrave;i lịch l&atilde;m, sang trọng, tinh tế v&agrave; kh&ocirc;ng k&eacute;m phần trẻ trung, thời thượng</span></div>\r\n','Một năm nữa lại sắp đi qua. Nhân dịp Giáng sinh và năm mới đang tới thật gần, Veneto đã update thêm thật nhiều sản phẩm mới tại các hệ thống showroom; cùng với hàng trăm items cực hấp dẫn để phục vụ nhu cầu mua sắm của khách hàng nhân dịp cuối năm 2015.','VENETO_POST_1712144132.jpg','/201610/','Veneto dành tặng ưu đãi \"cực khủng\" cho khách hàng dịp cuối năm','Veneto dành tặng ưu đãi \"cực khủng\" cho khách hàng dịp cuối năm',' khuyến mại, ưu đãi, veneto, quà tặng, 2015','Một năm nữa lại sắp đi qua. Nhân dịp Giáng sinh và năm mới đang tới thật gần, Veneto đã update thêm thật nhiều sản phẩm mới tại các hệ thống showroom; cùng với hàng trăm items cực hấp dẫn để phục vụ nhu cầu mua sắm của khách hàng nhân dịp cuối năm 2015.','Veneto dành tặng ưu đãi \"cực khủng\" cho khách hàng dịp cuối năm',0,0,0,0,1476431899,1476802632,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431699,1,NULL),(909,182,'VENETO FASHION SHOW - HONEY MOON & WEDDING PERFECT 2015','','veneto-fashion-show-honey-moon-wedding-perfect-2252-28275-article','{\"1476432037\":\"veneto-fashion-show-honey-moon-wedding-perfect-2015\"}','<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: justify;\"><span style=\"font-size:16px\"><strong>VENETO</strong>&nbsp;được biết đến như một trong c&aacute;c thương hiệu thời trang Nam cao cấp h&agrave;ng đầu H&agrave; Nội.&nbsp;Với phong c&aacute;ch lịch l&atilde;m, thời thượng v&agrave; đẳng cấp ,&nbsp;<strong>VENETO</strong>&nbsp;ra mắt Bộ sưu t&acirc;p vest cưới thời trang s&agrave;nh điệu .​<br />\r\n<br />\r\n<img alt=\"\" src=\"//veneto.vn/images/201610/veneto_fashion_show_2_0310124230.jpg\" style=\"border:none; height:733.781px; max-width:100%; width:1039px\" /><br />\r\n<br />\r\n<span style=\"color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif\"><strong>VENETO</strong>&nbsp;ấp ủ kế hoạch từ đầu năm, đ&atilde; tham gia l&agrave; phải chương tr&igrave;nh lớn v&agrave; ho&agrave;nh tr&aacute;ng nhất miền bắc tại kh&aacute;ch sạn 5 sao Melia H&agrave; Nội.&nbsp;Mọi thứ chuẩn bị đ&atilde; xong chỉ c&ograve;n chờ đến ng&agrave;y tr&igrave;nh diễn Bộ sưu tập mới tại Triển l&atilde;m cưới lớn nhất miền Bắc với gian h&agrave;ng đặc biệt.&nbsp;</span><span style=\"color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif\">Cơ hội cho c&ocirc; d&acirc;u ch&uacute; rể tại miền Bắc đến tham dự v&agrave; hưởng nhiều ưu đ&atilde;i với c&aacute;c thương hiệu cưới lớn nhất, uy t&iacute;n nhất!&nbsp;<br />\r\n<br />\r\nĐặc biệt,&nbsp;<strong>VENETO</strong>&nbsp;c&ograve;n t&agrave;i trợ tặng qu&agrave; cho c&ocirc; d&acirc;u, ch&uacute; rể ở chương tr&igrave;nh&nbsp;<strong>CẶP Đ&Ocirc;I HO&Agrave;N HẢO</strong>&nbsp;l&agrave; 10 bộ vest trị gi&aacute; l&ecirc;n tới: 30.000.000đ&nbsp;</span></span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\"><iframe align=\"middle\" frameborder=\"1\" height=\"500\" name=\"VENETO FASHION SHOW - HONEY MOON &amp; WEDDING PERFECT 2015.\" scrolling=\"yes\" src=\"http://www.youtube.com/embed/MsFlvciQiEc\" style=\"max-width: 100%;\" width=\"700\"></iframe></div>\r\n','VENETO được biết đến như một trong các thương hiệu thời trang Nam cao cấp hàng đầu Hà Nội. Với phong cách lịch lãm, thời thương và đẳng cấp , VENETO ra mắt BST vest cưới thời trang sành điệu','veneto_fashion_show_0310122440.jpg','/201610/','VENETO FASHION SHOW - HONEY MOON & WEDDING PERFECT 2015','VENETO FASHION SHOW - HONEY MOON & WEDDING PERFECT 2015','VENETO, FASHION SHOW','VENETO được biết đến như một trong các thương hiệu thời trang Nam cao cấp hàng đầu Hà Nội. Với phong cách lịch lãm, thời thương và đẳng cấp , VENETO ra mắt BST vest cưới thời trang sành điệu','VENETO FASHION SHOW - HONEY MOON & WEDDING PERFECT 2015',0,0,0,0,1476432019,1476432113,'quyettv','quyettv',NULL,0,NULL,NULL,NULL,1476431904,1,NULL),(910,178,'5 lý do bạn nên mua vest tại Veneto','','-ly-do-ban-nen-mua-vest-tai-veneto-2237-28276-article','{\"1476802685\":\"5-ly-do-ban-nen-mua-vest-tai-veneto\"}','<p><img alt=\"\" src=\"//veneto.vn/images/201610/BANNER_VENETO_0911170449.jpg\" style=\"border:none; font-family:arial,helvetica,sans-serif; font-size:12px; height:284px; line-height:20.8px; max-width:100%; text-align:center; width:700px\" /><br style=\"font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20.8px; text-align: center;\" />\r\n<br style=\"font-family: A