/*Table structure for table `customer_log` */

DROP TABLE IF EXISTS `customer_log`;

CREATE TABLE `customer_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `customer_id` int(11) NOT NULL,
  `action` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `object_class` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `object_pk` int(11) DEFAULT NULL,
  `created_at` int(11) NOT NULL,
  `is_success` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `fk_customer_id` (`customer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `customer_log` */

/*Table structure for table `info` */

DROP TABLE IF EXISTS `info`;

CREATE TABLE `info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `type` smallint(4) NOT NULL,
  `slug` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `is_active` tinyint(1) DEFAULT NULL,
  `status` smallint(4) DEFAULT NULL,
  `page_title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `h1` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_keywords` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `long_description` text COLLATE utf8_unicode_ci,
  `description` varchar(511) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `image_path` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `old_slugs` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` int(11) NOT NULL,
  `created_by` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `updated_at` int(11) DEFAULT NULL,
  `updated_by` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `UNIQUE2` (`slug`,`is_active`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `info` */

insert  into `info`(`id`,`name`,`type`,`slug`,`is_active`,`status`,`page_title`,`h1`,`meta_title`,`meta_description`,`meta_keywords`,`long_description`,`description`,`content`,`image`,`image_path`,`old_slugs`,`created_at`,`created_by`,`updated_at`,`updated_by`) values (11,'Giới thiệu về Veneto',2,'gioi-thieu-ve-veneto',1,NULL,'Giới thiệu về Veneto','Giới thiệu về Veneto','Giới thiệu về Veneto','','',NULL,NULL,'<div class=\"singer-entry\" style=\"width: 720px; font-size: 12px; margin:0 auto\">\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/1_2505162443.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/2_2505160725.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<span style=\"font-size:14px\"><span style=\"font-size:16px\"><strong>GIỚI THIỆU VỀ VENETO</strong></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n\r\n<div style=\"text-align: justify;\"><span style=\"font-size:14px\">Với mong muốn mang những trải nghiệm thời trang ho&agrave;n to&agrave;n mới, tạo n&ecirc;n một c&aacute;i nh&igrave;n kh&aacute;c biệt về thời trang; gi&uacute;p ph&aacute;i mạnh th&ecirc;m tự tin khi lựa chọn những sản phẩm thời trang cao cấp - năm 2013&nbsp;<strong>C&ocirc;ng ty TNHH Thời trang Veneto&nbsp;</strong>đ&atilde; được th&agrave;nh lập.</span><br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\"><span style=\"font-size:14px\">Sự quản l&yacute; chặt chẽ, chuy&ecirc;n nghiệp của đội ngũ quản l&yacute; c&ugrave;ng với tinh thần l&agrave;m việc trẻ trung, s&aacute;ng tạo kh&ocirc;ng ngừng của bộ phận thiết kế - sản xu&aacute;t; sự tận t&acirc;m, chất lượng phục vụ được đ&agrave;o tạo b&agrave;i bản của nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng&hellip; l&agrave; những yếu tố g&oacute;p phần l&agrave;m n&ecirc;n sự lớn mạnh của một trong những thương hiệu thời trang top đầu Việt Nam như&nbsp;<strong>Veneto</strong>&nbsp;hiện nay.</span></div>\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/3_2505131948.jpg\" style=\"border:none; height:456px; max-width:100%; width:700px\" />\r\n<div style=\"text-align: justify;\"><br />\r\n<span style=\"font-size:14px\">Được c&aacute;c kh&aacute;ch h&agrave;ng biết tới như một thương hiệu mạnh, chuy&ecirc;n cung cấp c&aacute;c sản phẩm thời trang nam thiết kế,&nbsp;<strong>Veneto&nbsp;</strong>hiện đ&atilde; cho ra mắt thị trường h&agrave;ng ng&agrave;n sản phẩm đa dạng về kiểu d&aacute;ng, mẫu m&atilde;: từ những trang phục độc t&ocirc;n của c&aacute;nh m&agrave;y r&acirc;u như:&nbsp;<em>thời trang c&ocirc;ng sở, vest cưới, &aacute;o sơ mi, quần t&acirc;y,&hellip;&nbsp;</em>cho đến c&aacute;c trang phục thời trang như:&nbsp;<em>&aacute;o thun, &aacute;o kho&aacute;c, &aacute;o len, quần kaki&hellip;</em>&nbsp;c&ugrave;ng với c&aacute;c phụ kiện thời trang đều được thiết kế một c&aacute;ch ho&agrave;n hảo, hiện đại.<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i lu&ocirc;n quan niệm thời trang l&agrave; sự t&igrave;m t&ograve;i v&agrave; s&aacute;ng tạo kh&ocirc;ng ngừng; ch&iacute;nh v&igrave; l&yacute; do ấy m&agrave; c&aacute;c sản phẩm thời trang của&nbsp;<strong>Veneto</strong>&nbsp;đều được thiết kế với phong c&aacute;ch lịch l&atilde;m, m&agrave;u sắc trẻ trung, hiện đại lu&ocirc;n theo kịp những xu hướng thời trang tr&ecirc;n thế giới.&nbsp;<br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/4_2005093538.jpg\" style=\"border:none; height:auto !important; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<span style=\"font-size:16px\"><strong>TẦM NH&Igrave;N &ndash; SỨ MỆNH</strong></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n<span style=\"font-size:14px\">Việt Nam đang hội nhập với bạn b&egrave; quốc tế, mở ra nhiều cơ hội mới, v&igrave; vậy h&igrave;nh ảnh ph&aacute;i nam cần trở n&ecirc;n lịch l&atilde;m hơn, phong c&aacute;ch hơn để h&ograve;a nhập với sự ph&aacute;t triển của thế giới v&agrave; c&aacute;c xu hướng thời trang đang thịnh h&agrave;nh.<strong>Veneto&nbsp;</strong>lu&ocirc;n nỗ lực v&agrave; s&aacute;ng tạo kh&ocirc;ng ngừng để tạo ra những sản phẩm thời trang nam cao cấp, đ&aacute;p ứng tốt nhu cầu, thị hiếu của nam giới.<br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/5_2505132147.jpg\" style=\"border:none; height:456px; max-width:100%; width:700px\" /><br />\r\n<br />\r\nB&ecirc;nh cạnh hoạt động kinh doanh, đội ngũ quản l&yacute; của Veneto mong muốn tạo ra một mội trường l&agrave;m việc hiện đại: ph&aacute;t huy sự năng động, nhiệt huyết v&agrave; hiệu quả cho c&aacute;c bạn nh&acirc;n vi&ecirc;n trẻ.<br />\r\n<br />\r\n<strong>Veneto</strong>&nbsp;đặc biệt quan t&acirc;m v&agrave; lu&ocirc;n hướng tới cộng đồng, ch&uacute;ng t&ocirc;i lu&ocirc;n cố gắng đ&oacute;ng g&oacute;p cho x&atilde; hội những gi&aacute; trị hữu &iacute;ch nhất, thiết thực nhất từ ch&iacute;nh kết quả của hoạt động kinh doanh của c&ocirc;ng ty.</span><br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/6_2005094459.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n&nbsp;<br />\r\n<span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:14px\"><span style=\"font-size:16px\"><strong>GI&Aacute; TRỊ CỐT L&Otilde;I</strong></span></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n<strong>Veneto&nbsp;</strong><span style=\"color:rgb(0, 0, 0); font-size:14px\">x&acirc;y dựng h&igrave;nh ảnh thời trang nam kh&aacute;c biệt. Đề cao yếu tố sang trọng, lịch l&atilde;m trong từng đường n&eacute;t thiết kế - chất liệu của sản phẩm. Ch&uacute;ng t&ocirc;i lu&ocirc;n nỗ lực mang những sản phẩm thời trang cao cấp đến gần hơn với b&agrave;n tay người ti&ecirc;u d&ugrave;ng tr&ecirc;n khắp cả nước v&agrave; vươn xa hơn tại thị trường thế giới n&oacute;i chung, khu vực Đ&ocirc;ng Nam &Aacute; n&oacute;i ri&ecirc;ng.</span>&nbsp;<br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\"><strong><img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/7_2005101243.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<span style=\"font-size:16px\">CAM KẾT VỀ CHẤT LƯỢNG SẢN PHẨM</span></strong></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">&nbsp;<br />\r\nVới bề d&agrave;y kinh nghiệm v&agrave; &uacute;y t&iacute;n tr&ecirc;n tr&ecirc;n thị trường thời trang, gi&aacute; trị của c&aacute;c mặt h&agrave;ng được sản xuất thủ c&ocirc;ng tạo ra đ&atilde; được khẳng định bằng những đ&aacute;nh gi&aacute; tốt từ ph&iacute;a người ti&ecirc;u d&ugrave;ng. Sản phẩm thời trang của&nbsp;<strong>Veneto</strong>đem lại sự thoải m&aacute;i, diện mạo sang trọng, lịch l&atilde;m cho người sử dụng. Được sản xuất từ những kiệt t&aacute;c về kỹ thuật may đo, c&aacute;c sản phẩm thời trang của Veneto l&agrave; c&acirc;n bằng ho&agrave;n hảo của c&aacute;c yếu tố:&nbsp;<em><strong>thời trang, phong c&aacute;ch, lịch sử, văn h&oacute;a.&nbsp;</strong></em>&nbsp;&nbsp;&nbsp;<br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/8_2505132356.jpg\" style=\"border:none; height:456px; max-width:100%; width:700px\" /><br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i sử dụng loại vải cao cấp nhập khẩu từ H&agrave;n Quốc: đảm bảo sự bền l&acirc;u về m&agrave;u sắc, form d&aacute;ng của sản phẩm cũng như gi&uacute;p người mặc c&oacute; cảm gi&aacute;c dễ chịu khi diện đồ.<br />\r\n&nbsp;</span></span><br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/9_2005103511.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<span style=\"font-size:16px\"><span style=\"color:rgb(0, 0, 0)\"><strong>HOẠT ĐỘNG X&Atilde; HỘI</strong></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n<span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">B&ecirc;n cạnh hoạt động kinh doanh ng&agrave;nh h&agrave;ng thời trang,&nbsp;<strong>C&ocirc;ng ty TNHH Thời trang Veneto</strong>&nbsp;cũng t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội, c&aacute;c chương tr&igrave;nh cộng đồng. Trong những năm qua, thương hiệu Veneto đ&atilde; tham gia t&agrave;i trợ cho c&aacute;c chương tr&igrave;nh, c&aacute;c cuộc thi tại một số trường Đại học như:&nbsp;<em>ĐH Ngoại thương, ĐH Kinh doanh &ndash; C&ocirc;ng nghệ, ĐH Thương mại,&hellip;</em>&nbsp;gi&uacute;p c&aacute;c bạn sinh vi&ecirc;n tỏa s&aacute;ng, thực hiện ước mơ của bản th&acirc;n.<br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/10_2505132615.jpg\" style=\"border:none; height:456px; max-width:100%; width:700px\" /><br />\r\n<br />\r\nĐội ngũ c&aacute;n bộ, nh&acirc;n vi&ecirc;n c&ocirc;ng ty cũng đ&atilde; c&oacute; những chuyến đi tới c&aacute;c bản l&agrave;ng v&ugrave;ng cao, tổ chức những chương tr&igrave;nh thiện nguyện, gi&uacute;p đỡ trẻ em ngh&egrave;o &ndash; kh&ocirc;ng được đến trường; những gia đ&igrave;nh ho&agrave;n cảnh kh&oacute; khăn. Với mong muốn san sẻ phần n&agrave;o những kh&oacute; khăn, gi&uacute;p x&atilde; hội ng&agrave;y c&agrave;ng gi&agrave;u mạnh&nbsp;<strong>Veneto</strong>&nbsp;đ&atilde; hiện thực h&oacute;a những mong muốn ấy bằng h&agrave;nh động c&oacute; &iacute;ch cho cộng đồng.</span></span><br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/11_2005110430.jpg\" style=\"border:none; height:490px; max-width:100%; width:700px\" /><br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:14px\"><span style=\"font-size:16px\"><span style=\"color:rgb(0, 0, 0)\"><strong>HỆ THỐNG SHOWROOM</strong></span></span></span>\r\n\r\n<div>\r\n<hr /></div>\r\n<span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:14px\">Hiện tại&nbsp;<strong>Veneto</strong>&nbsp;đ&atilde; c&oacute; 10 cửa h&agrave;ng sang trọng, quy m&ocirc; nằm tại H&agrave; Nội v&agrave; c&aacute;c tỉnh th&agrave;nh kh&aacute;c với hơn 16 đại l&yacute; cấp 2.&nbsp;</span></span><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0)\">Veneto kh&ocirc;ng ngừng vươn m&igrave;nh, mở rộng mạng lưới chi nh&aacute;nh tr&ecirc;n khắp cả nước; đưa c&aacute;c sản phẩm thời trang cao cấp tới gần hơn b&agrave;n tay người ti&ecirc;u d&ugrave;ng.<br />\r\n<br />\r\n<img alt=\"\" src=\"http://veneto-quyettv.rhcloud.com/frontend/web/images/201610/12_2505132931.jpg\" style=\"border:none; height:929px; max-width:100%; width:700px\" /><br />\r\n&nbsp;<br />\r\nQuan trọng hơn, kh&ocirc;ng chỉ đặt mục ti&ecirc;u về số lượng, Veneto đặc biệt ch&uacute; trọng h&agrave;ng đấu đến chất lượng của từng showroom: thiết kế, c&aacute;ch b&agrave;y tr&iacute; sản phẩm v&agrave; quan trọng nhất đ&oacute; l&agrave; chất lượng phục vụ.<br />\r\n<br />\r\nĐể l&agrave;m h&agrave;i l&ograve;ng v&agrave; thỏa m&atilde;n tốt nhất kh&aacute;ch h&agrave;ng đến mua sắm, ch&uacute;ng t&ocirc;i gi&aacute;m s&aacute;t chặt chẽ to&agrave;n bộ hệ thống showroom tr&ecirc;n to&agrave;n quốc; lu&ocirc;n lu&ocirc;n đặt chỉ ti&ecirc;u, khuyến kh&iacute;ch nh&acirc;n vi&ecirc;n. B&ecirc;n cạnh đ&oacute;, Veneto thường xuy&ecirc;n tổ chức c&aacute;c hoạt động traning kỹ năng tư vấn, phục vụ kh&aacute;ch h&agrave;ng cho nh&acirc;n vi&ecirc;n; kiểm tra, đ&aacute;nh gi&aacute; thường kỳ. Với những sự đầu tư đ&oacute;, Veneto kỳ vọng sẽ lu&ocirc;n l&agrave; địa chỉ mua sắm tin cậy, l&agrave; h&igrave;nh ảnh đầu ti&ecirc;n trong t&acirc;m tr&iacute; kh&aacute;ch h&agrave;ng khi nghĩ về thời trang nam</span></span><br />\r\n<br />\r\n<br />\r\n<strong><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">HỆ THỐNG SHOWROOM CỦA VENETO</span></strong>\r\n\r\n<hr />\r\n<div style=\"margin-left: 40px;\"><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 1 : 27 QU&Aacute;N TH&Aacute;NH - HN</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 2 : 259 CH&Ugrave;A BỘC - HN</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 3 : 126 CẦU GIẤY - HN</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 4 : 93 XU&Acirc;N THỦY &ndash; HN</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 5: 19 PHỐ HUẾ - HN&nbsp;</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 6 : 32 TRẦN HƯNG ĐẠO - HẢI DƯƠNG</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 7 : 52 M&Ecirc; LINH - L&Ecirc; CH&Acirc;N - HẢI PH&Ograve;NG</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 8 : HẠ LONG - QUẢNG NINH (OPENING SOON)</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 9 : 316 L&Ecirc; VĂN SỸ - TP.HCM</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">■ STORE 10 : VI&Ecirc;NG CHĂN - L&Agrave;O</span></div>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"face-book\" style=\"float: left; width: 720px; clear: both; font-family: Arial, Helvetica, sans-serif; font-size: 12px;\">&nbsp;</div>\r\n','','/201610/',NULL,1475920768,'quyettv',1475921731,'quyettv'),(12,'Chính sách đại lý',5,'chinh-sach-dai-ly',1,NULL,'Chính sách đại lý','Chính sách đại lý','Chính sách đại lý','','',NULL,NULL,'<p>Ch&iacute;nh s&aacute;ch hợp t&aacute;c b&aacute;n h&agrave;ng đại l&yacute;</p>\r\n','','/201610/',NULL,1476029598,'quyettv',NULL,NULL);

