  `malterial` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `style` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `use_duration` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
